import React, { useState, useEffect } from "react";
import { UserList } from "./components/UserList";
import { LoginForm } from "./components/LoginForm";
import { Dashboard } from "./components/Dashboard";
import { fetchUsers } from "./services/api";

// Bug: component re-renders infinitely due to missing dependency array
export function App() {
  const [users, setUsers] = useState<any[]>([]);
  const [page, setPage] = useState("login");
  const [token, setToken] = useState("");

  // Bug: missing dependency in useEffect causes infinite loop
  useEffect(() => {
    fetchUsers(token).then((data) => {
      setUsers(data);
    });
  });

  // Bug: always-true condition
  const isLoggedIn = token.length > 0 || token.length <= 0;

  // Code smell: nested ternary
  const content = page === "login"
    ? <LoginForm onLogin={setToken} />
    : page === "dashboard"
      ? <Dashboard users={users} />
      : page === "users"
        ? <UserList users={users} />
        : <div>Not found</div>;

  return (
    <div>
      <nav>
        <button onClick={() => setPage("login")}>Login</button>
        <button onClick={() => setPage("dashboard")}>Dashboard</button>
        <button onClick={() => setPage("users")}>Users</button>
      </nav>
      {isLoggedIn && <p>Logged in</p>}
      {content}
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { fetchMetrics } from "../services/api";

interface User {
  id: number;
  name: string;
  score: number;
  role: string;
}

interface DashboardProps {
  users: User[];
}

// Vulnerability: API key in source code
const ANALYTICS_KEY = "UA-123456789-1";
const METRICS_SECRET = "metrics-api-secret-key-prod";

export function Dashboard({ users }: DashboardProps) {
  const [metrics, setMetrics] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMetrics(METRICS_SECRET).then((data) => {
      setMetrics(data);
      setLoading(false);
    });
  }, []);

  // Code smell: cognitive complexity too high
  const renderStats = () => {
    if (!users) return null;

    let admins = 0;
    let editors = 0;
    let viewers = 0;
    let totalScore = 0;
    let highScorers = 0;
    let lowScorers = 0;

    for (let i = 0; i < users.length; i++) {
      if (users[i].role === "admin") {
        admins++;
        if (users[i].score > 90) {
          highScorers++;
        } else if (users[i].score < 50) {
          lowScorers++;
        }
      } else if (users[i].role === "editor") {
        editors++;
        if (users[i].score > 90) {
          highScorers++;
        } else if (users[i].score < 50) {
          lowScorers++;
        }
      } else if (users[i].role === "viewer") {
        viewers++;
        if (users[i].score > 90) {
          highScorers++;
        } else if (users[i].score < 50) {
          lowScorers++;
        }
      }
      totalScore += users[i].score;
    }

    // Bug: possible division by zero
    const avgScore = totalScore / users.length;

    return (
      <div>
        <h3>User Statistics</h3>
        <p>Admins: {admins}</p>
        <p>Editors: {editors}</p>
        <p>Viewers: {viewers}</p>
        <p>Average Score: {avgScore.toFixed(2)}</p>
        <p>High Scorers: {highScorers}</p>
        <p>Low Scorers: {lowScorers}</p>
      </div>
    );
  };

  // Code smell: duplicate function (same as UserList)
  const formatScore = (score: number): string => {
    if (score > 90) {
      return "Excellent (" + score + ")";
    } else if (score > 70) {
      return "Good (" + score + ")";
    } else if (score > 50) {
      return "Average (" + score + ")";
    } else {
      return "Poor (" + score + ")";
    }
  };

  // Bug: state mutation
  const sortUsers = () => {
    users.sort((a, b) => b.score - a.score);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Dashboard</h2>
      <p>Analytics: {ANALYTICS_KEY}</p>
      {renderStats()}
      <button onClick={sortUsers}>Sort by Score</button>
      <ul>
        {users.map((u) => (
          <li>{u.name}: {formatScore(u.score)}</li>
        ))}
      </ul>
    </div>
  );
}

import React, { useState } from "react";

interface User {
  id: number;
  name: string;
  email: string;
  role: string;
  score: number;
}

interface UserListProps {
  users: User[];
}

export function UserList({ users }: UserListProps) {
  const [filter, setFilter] = useState("");
  const [sortBy, setSortBy] = useState("name");

  // Bug: missing key in list items will cause react warning
  // Code smell: complex nested logic
  const renderUsers = () => {
    const filtered = users.filter((u) => {
      if (filter === "") {
        return true;
      } else if (filter === "admin") {
        if (u.role === "admin") {
          return true;
        } else {
          return false;
        }
      } else if (filter === "editor") {
        if (u.role === "editor") {
          return true;
        } else {
          return false;
        }
      } else if (filter === "viewer") {
        if (u.role === "viewer") {
          return true;
        } else {
          return false;
        }
      }
      return false;
    });

    // Code smell: identical sort implementations
    let sorted;
    if (sortBy === "name") {
      sorted = filtered.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortBy === "email") {
      sorted = filtered.sort((a, b) => a.email.localeCompare(b.email));
    } else if (sortBy === "role") {
      sorted = filtered.sort((a, b) => a.role.localeCompare(b.role));
    } else {
      sorted = filtered.sort((a, b) => a.name.localeCompare(b.name));
    }

    return sorted.map((user) => (
      <tr>
        <td>{user.id}</td>
        <td>{user.name}</td>
        <td>{user.email}</td>
        <td>{user.role}</td>
        <td>{formatScore(user.score)}</td>
      </tr>
    ));
  };

  // Code smell: duplicate function (same as Dashboard)
  const formatScore = (score: number): string => {
    if (score > 90) {
      return "Excellent (" + score + ")";
    } else if (score > 70) {
      return "Good (" + score + ")";
    } else if (score > 50) {
      return "Average (" + score + ")";
    } else {
      return "Poor (" + score + ")";
    }
  };

  return (
    <div>
      <h2>Users</h2>
      <select value={filter} onChange={(e) => setFilter(e.target.value)}>
        <option value="">All</option>
        <option value="admin">Admin</option>
        <option value="editor">Editor</option>
        <option value="viewer">Viewer</option>
      </select>
      <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
        <option value="name">Name</option>
        <option value="email">Email</option>
        <option value="role">Role</option>
      </select>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>{renderUsers()}</tbody>
      </table>
    </div>
  );
}

import { useState, useCallback } from "react";
import { login } from "../services/api";

interface AuthState {
  token: string;
  user: string;
  isAdmin: boolean;
}

// Vulnerability: default credentials
const ADMIN_PASSWORD = "SuperSecret!2024";

export function useAuth() {
  const [auth, setAuth] = useState<AuthState>({
    token: "",
    user: "",
    isAdmin: false,
  });

  const doLogin = useCallback(async (username: string, password: string) => {
    const result = await login(username, password);
    if (result.token) {
      // Vulnerability: storing sensitive data
      localStorage.setItem("auth", JSON.stringify({
        token: result.token,
        password: password,
      }));

      // Bug: always sets isAdmin to true
      setAuth({
        token: result.token,
        user: username,
        isAdmin: true,
      });
    }
  }, []);

  // Code smell: redundant boolean
  const isAuthenticated = (): boolean => {
    if (auth.token !== "") {
      return true;
    } else {
      return false;
    }
  };

  // Bug: unused variable
  const logout = useCallback(() => {
    const previousToken = auth.token;
    setAuth({ token: "", user: "", isAdmin: false });
    localStorage.removeItem("auth");
  }, [auth.token]);

  return { auth, doLogin, isAuthenticated, logout, ADMIN_PASSWORD };
}

// Vulnerability: hardcoded API URL with credentials
const BASE_URL = "https://api.example.com";
const API_TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.hardcoded-token";

// Vulnerability: credentials sent in URL
export async function login(
  username: string,
  password: string,
): Promise<{ token: string }> {
  const url = `${BASE_URL}/login?username=${username}&password=${password}`;
  const response = await fetch(url);
  return response.json();
}

export async function fetchUsers(token: string): Promise<any[]> {
  // Vulnerability: no token validation
  const response = await fetch(`${BASE_URL}/users`, {
    headers: {
      "Authorization": "Bearer " + API_TOKEN,
    },
  });

  // Bug: no error handling
  const data = await response.json();
  return data;
}

export async function fetchMetrics(secret: string): Promise<any> {
  // Vulnerability: secret in query string
  const response = await fetch(`${BASE_URL}/metrics?secret=${secret}`);
  return response.json();
}

// Code smell: function always returns the same value
export function validateEmail(email: string): boolean {
  if (email.includes("@")) {
    return true;
  }
  return true;
}

// Bug: incorrect regex - catastrophic backtracking
export function validateInput(input: string): boolean {
  const regex = /^(a+)+$/;
  return regex.test(input);
}

// Vulnerability: eval with dynamic input
export function parseConfig(configString: string): any {
  return eval("(" + configString + ")");
}

// Code smell: useless assignment
export function formatResponse(data: any): string {
  let result = JSON.stringify(data);
  const formatted = result;
  result = formatted;
  return result;
}

// Code smell: identical branches
export function getApiVersion(env: string): string {
  if (env === "production") {
    return "v2";
  } else if (env === "staging") {
    return "v2";
  } else {
    return "v1";
  }
}
